muclient.script:
require "tprint"

function plook_command(aliasname, aliasline, wildcards)
	query = string.lower(wildcards[1])
	--print("plook_command called with variables:\n name: " .. aliasname .. "\n line: " .. aliasline .. "\n wildcard1: " .. query)
	results = {}
	local found = false
	for k, v in ipairs(mob_list) do
		local match = nil
		match = string.find(string.lower(mob_list[k]["name"]), query, 1, true)
		if match then
			found = true
			results[#results + 1] = {
				["name"] = mob_list[k]["name"],
				["poolnum"] = mob_list[k]["poolnum"]
			}
		end
	end
	if found then
		tprint(results)
	else
		print("No results found.")
	end
end

